// Erwartet pro Item ein Feld `ai_output` (String ODER Objekt) + irgendwo eine PolicyID.
// Gibt pro Item einen flachen JSON-Payload zurück:
// { "ChangeAddress", Street, City, PostalCode }

function stripCodeFences(s) {
  if (typeof s !== 'string') return s;
  return s
    .replace(/^```(?:json)?\s*/i, '')
    .replace(/```$/, '');
}

function fixTrailingCommas(s) {
  if (typeof s !== 'string') return s;
  // Entfernt abschließende Kommata vor schließenden Klammern/Braces
  return s.replace(/,\s*([}\]])/g, '$1');
}

function normalizeQuotes(s) {
  if (typeof s !== 'string') return s;
  // Smart Quotes → normale Anführungszeichen
  return s.replace(/[“”]/g, '"').replace(/[‘’]/g, "'");
}

function tryParseLenient(input) {
  if (typeof input !== 'string') return input;
  let s = input.trim();
  s = stripCodeFences(s);
  s = normalizeQuotes(s);
  s = fixTrailingCommas(s);
  try {
    return JSON.parse(s);
  } catch {
    return null;
  }
}

function extractFromLooseText(text) {
  if (typeof text !== 'string') return { Street: '', City: '', PostalCode: '' };

  const get = (patterns) => {
    for (const p of patterns) {
      const m = text.match(p);
      if (m && m[1]) return m[1].trim();
    }
    return '';
  };

  const Street = get([
    /"Street"\s*:\s*"([^"]+)"/i,
    /Street\s*[:=]\s*([^,\n]+)\b/i,
    /"address"\s*:\s*"([^"]+)"/i,
    /address\s*[:=]\s*([^,\n]+)\b/i,
  ]);

  const City = get([
    /"City"\s*:\s*"([^"]+)"/i,
    /City\s*[:=]\s*([^,\n]+)\b/i,
    /"town"\s*:\s*"([^"]+)"/i,
    /town\s*[:=]\s*([^,\n]+)\b/i,
  ]);

  const PostalCode = get([
    /"PostalCode"\s*:\s*"([^"]+)"/i,
    /PostalCode\s*[:=]\s*([A-Za-z0-9\- ]+)/i,
    /"zip(code)?"\s*:\s*"([^"]+)"/i,
    /zip(code)?\s*[:=]\s*([A-Za-z0-9\- ]+)/i,
    /"plz"\s*:\s*"([^"]+)"/i,
    /plz\s*[:=]\s*([A-Za-z0-9\- ]+)/i,
  ]);

  return { Street, City, PostalCode };
}

function pickFirst(...vals) {
  for (const v of vals) {
    if (v !== undefined && v !== null && String(v).trim() !== '') return String(v).trim();
  }
  return '';
}

return items.map(item => {
  const src = item.json.ai_output ?? item.json.output ?? item.json.ai ?? item.json; // wo die Agent-Antwort liegt

  // PolicyID bestimmen (Pfad anpassen, falls nötig)
  const policyFromContext = pickFirst(
    item.json.policyId,
    item.json.PolicyID,
    (typeof src === 'object' && src) ? src.policyId : undefined,
    (typeof src === 'object' && src) ? src.PolicyID : undefined,
  );

  let obj = null;

  // A) Bereits Objekt?
  if (src && typeof src === 'object' && ('Street' in src || 'City' in src || 'PostalCode' in src)) {
    obj = src;
  }

  // B) JSON-String → lenient parsen
  if (!obj && typeof src === 'string') {
    const parsed = tryParseLenient(src);
    if (parsed && typeof parsed === 'object') obj = parsed;
  }

  // C) Fallback: Freitext extrahieren
  let Street = '';
  let City = '';
  let PostalCode = '';

  if (obj) {
    // Synonyme/alternative Keys berücksichtigen
    Street = pickFirst(obj.Street, obj.street, obj.Address, obj.address, obj.streetAddress, obj["Street Address"]);
    City = pickFirst(obj.City, obj.city, obj.Town, obj.town);
    PostalCode = pickFirst(obj.PostalCode, obj.postalCode, obj.zip, obj.zipcode, obj.ZIP, obj.plz, obj.PLZ);
  } else {
    const text = typeof src === 'string' ? src : String(src ?? '');
    const ext = extractFromLooseText(text);
    Street = ext.Street;
    City = ext.City;
    PostalCode = ext.PostalCode;
  }

  // Minimal-Validierung
  if (!Street || !City || !PostalCode) {
    // Für Debugging hilfreich:
    // throw new Error(`Adresse unvollständig. Street="${Street}", City="${City}", PostalCode="${PostalCode}". Quelle: ${typeof src === 'string' ? src.slice(0, 500) : JSON.stringify(src)}`);
    // Statt Fehler: leere Felder zulassen – passe nach Bedarf an:
  }


  return {
    json: {
      Street,
      City,
      PostalCode
    }
  };
});

