function extractFromLooseText(text) {
  if (typeof text !== 'string') return { PolicyID: '', Function: '' };

  // Beispiel: {"PolicyID": "92863673", "Function": "ChangeAddress"} ohne sauberes JSON
  // oder: PolicyID: 92863673; Function: ChangeAddress
  // oder: Zeilenweise Ausgabe etc.

  // 1) PolicyID: Zahl oder alphanumerisch
  const idMatch =
    text.match(/"PolicyID"\s*:\s*"([^"]+)"/i) ||
    text.match(/PolicyID\s*[:=]\s*([A-Za-z0-9_-]+)/i);

  // 2) Function: Wort oder String
  const fnMatch =
    text.match(/"Function"\s*:\s*"([^"]+)"/i) ||
    text.match(/Function\s*[:=]\s*([A-Za-z0-9_-]+)/i);

  return {
    PolicyID: idMatch ? idMatch[1] : '',
    Function: fnMatch ? fnMatch[1] : '',
  };
}

return items.map(item => {
  const src = item.json.ai_output ?? item.json.output ?? item.json; // wo deine Antwort liegt

  // Fall A: bereits Objekt mit PolicyID/Function
  if (src && typeof src === 'object' && ('PolicyID' in src || 'Function' in src)) {
    const PolicyID = String(src.PolicyID ?? '').trim();
    const FunctionName = String(src.Function ?? '').trim();
    if (!PolicyID) {
      throw new Error('PolicyID ist leer (Objekt-Fall). Prüfe die Quelle von ai_output.');
    }
    return { json: { PolicyID, Function: FunctionName || 'ChangeAddress' } };
  }

  // Fall B: JSON-String -> parsen
  const parsed = tryParseJsonMaybe(src);
  if (parsed && typeof parsed === 'object') {
    const PolicyID = String(parsed.PolicyID ?? '').trim();
    const FunctionName = String(parsed.Function ?? '').trim();
    if (!PolicyID) {
      throw new Error('PolicyID ist leer (geparster JSON-String).');
    }
    return { json: { PolicyID, Function: FunctionName || 'ChangeAddress' } };
  }

  // Fall C: Fallback – rohe Textausgabe robust parsen
  const text = typeof src === 'string' ? src : String(src ?? '');
  const { PolicyID, Function: FunctionName } = extractFromLooseText(text);

  if (!PolicyID) {
    // letzte Rettung: vielleicht liegt die ID in einem anderen Feld (z. B. customerId/policyId)
    const fallbackId = item.json.policyId ?? item.json.customerId ?? '';
