      // Fürs Debugging alles mitschicken
      throw new Error(`Konnte PolicyID nicht extrahieren. Quelle: ${JSON.stringify(src)}`);
    }
    return { json: { PolicyID: String(fallbackId), Function: FunctionName || 'ChangeAddress' } };
  }

  return { json: { PolicyID, Function: FunctionName || 'ChangeAddress' } };
});

